'use strict';

var View = require( 'djed/interface/view/View' );

module.exports = View.extend( {

  events: {
    'click': 'toggleModel'
  },

  initialize: function ( options ) {
    this.toggle = options.toggle;
    this.listenTo( this.toggle, 'change', this.render );
  },

  toggleModel: function () {
    this.toggle.toggle();
  },

  render: function () {

  }

} );
