'use strict';

var View = require( 'djed/interface/view/View' );
var isElementInViewport = require( 'djed/util/isElementInViewport' );

module.exports = View.extend( {

  initialize: function () {
    var self = this;
    this.inview = new window.Waypoint.Inview( {
      element: this.$el,
      enter: function ( direction ) {
        self.trigger( 'enter', direction );
      },
      entered: function ( direction ) {
        self.trigger( 'entered', direction );
      },
      exit: function ( direction ) {
        self.trigger( 'exit', direction );
      },
      exited: function ( direction ) {
        self.trigger( 'exited', direction );
      }
    } );
  },

  isInView: function () {
    return isElementInViewport( this.$el );
  }

} );
